  1→'use client'
  2→
  3→import { useEffect, useRef } from 'react'
  4→import { GooglePlace, GoogleAutocompleteOptions } from '@/types/google-maps'
  5→
  6→export const useGooglePlaces = (
  7→  onPlaceSelect: (place: GooglePlace) => void,
  8→  options: GoogleAutocompleteOptions = {}
  9→) => {
 10→  const inputRef = useRef<HTMLInputElement>(null)
 11→  const autocompleteRef = useRef<unknown>(null)
 12→
 13→  useEffect(() => {
 14→    if (!inputRef.current) return
 15→
 16→    const initAutocomplete = () => {
 17→      if (window.google && window.google.maps && window.google.maps.places && inputRef.current) {
 18→        try {
 19→          const defaultOptions = {
 20→            componentRestrictions: { country: 'AU' },
 21→            fields: ['formatted_address', 'geometry', 'place_id'],
 22→            types: ['address']
 23→          }
 24→
 25→          if (!window.google?.maps?.places?.Autocomplete) return
 26→          
 27→          autocompleteRef.current = new window.google.maps.places.Autocomplete(
 28→            inputRef.current,
 29→            { ...defaultOptions, ...options }
 30→          )
 31→
 32→          const autocomplete = autocompleteRef.current as { 
 33→            addListener: (event: string, callback: () => void) => void
 34→            getPlace: () => GooglePlace 
 35→          }
 36→
 37→          autocomplete.addListener('place_changed', () => {
 38→            try {
 39→              const place = autocomplete.getPlace()
 40→              if (place && place.formatted_address) {
 41→                onPlaceSelect(place)
 42→              }
 43→            } catch (error) {
 44→              console.log('Place selection error:', error)
 45→            }
 46→          })
 47→        } catch (error) {
 48→          console.log('Autocomplete initialization error:', error)
 49→        }
 50→      }
 51→    }
 52→
 53→    // Wait for Google Maps to be fully loaded
 54→    const waitForGoogle = () => {
 55→      return new Promise<void>((resolve) => {
 56→        console.log('Waiting for Google Maps API...', {
 57→          google: !!window.google,
 58→          maps: !!(window.google && window.google.maps),
 59→          places: !!(window.google && window.google.maps && window.google.maps.places)
 60→        })
 61→        
 62→        if (window.google && window.google.maps && window.google.maps.places) {
 63→          console.log('Google Maps API ready!')
 64→          resolve()
 65→        } else {
 66→          let attempts = 0
 67→          const checkInterval = setInterval(() => {
 68→            attempts++
 69→            console.log(`Checking Google Maps API... Attempt ${attempts}`, {
 70→              google: !!window.google,
 71→              maps: !!(window.google && window.google.maps),
 72→              places: !!(window.google && window.google.maps && window.google.maps.places)
 73→            })
 74→            
 75→            if (window.google && window.google.maps && window.google.maps.places) {
 76→              console.log('Google Maps API loaded after', attempts, 'attempts')
 77→              clearInterval(checkInterval)
 78→              resolve()
 79→            }
 80→            
 81→            // Stop trying after 50 attempts (5 seconds)
 82→            if (attempts >= 50) {
 83→              console.warn('Google Maps API failed to load after 5 seconds')
 84→              clearInterval(checkInterval)
 85→              resolve()
 86→            }
 87→          }, 100)
 88→        }
 89→      })
 90→    }
 91→
 92→    waitForGoogle().then(() => {
 93→      console.log('Initializing autocomplete...')
 94→      setTimeout(initAutocomplete, 100) // Small delay to ensure everything is ready
 95→    })
 96→
 97→    return () => {
 98→      if (autocompleteRef.current && window.google?.maps?.event) {
 99→        try {
100→          window.google.maps.event.clearInstanceListeners(autocompleteRef.current)
101→        } catch (error) {
102→          console.log('Cleanup error:', error)
103→        }
104→      }
105→    }
106→  }, [onPlaceSelect, options])
107→
108→  return inputRef
109→}

