  3→import { useEffect, useRef } from 'react'
  4→import '@/types/google-maps'
  5→
  6→interface GoogleMapProps {
  7→  address: string
  8→  className?: string
  9→  zoom?: number
 10→}
 11→
 12→export default function GoogleMap({ address, className = '', zoom = 15 }: GoogleMapProps) {
 13→  const mapRef = useRef<HTMLDivElement>(null)
 14→  const mapInstanceRef = useRef<unknown>(null)
 15→
 16→  useEffect(() => {
 17→    console.log('GoogleMap component mounted, API Key:', process.env.NEXT_PUBLIC_GOOGLE_MAPS_API_KEY ? 'Available' : 'Missing');
 18→    
 19→    const initMap = () => {
 20→      if (!window.google || !mapRef.current) {
 21→        console.log('Google Maps API not ready:', { google: !!window.google, mapRef: !!mapRef.current });
 22→        return;
 23→      }
 24→
 25→      try {
 26→        if (!window.google?.maps?.Geocoder) return
 27→        const geocoder = new window.google.maps.Geocoder()
 28→        
 29→        geocoder.geocode({ address }, (results, status) => {
 30→          if (status === 'OK' && results && results.length > 0) {
 31→            const result = results[0] as { geometry: { location: unknown } }
 32→            const location = result.geometry.location
 33→            
 34→            const mapOptions = {
 35→              zoom,
 36→              center: location,
 37→              mapTypeId: 'roadmap',
 38→              scrollwheel: false,
 39→              navigationControl: false,
 40→              mapTypeControl: false,
 41→              scaleControl: false,
 42→              draggable: true,
 43→              styles: [
 44→                {
 45→                  featureType: 'administrative',
 46→                  elementType: 'labels.text.fill',
 47→                  stylers: [{ color: '#dc2626' }]
 48→                },
 49→                {
 50→                  featureType: 'landscape',
 51→                  elementType: 'all',
 52→                  stylers: [{ color: '#f2f2f2' }]
 53→                },
 54→                {
 55→                  featureType: 'poi',
 56→                  elementType: 'all',
 57→                  stylers: [{ visibility: 'off' }]
 58→                },
 59→                {
 60→                  featureType: 'road',
 61→                  elementType: 'all',
 62→                  stylers: [{ saturation: -100 }, { lightness: 45 }]
 63→                },
 64→                {
 65→                  featureType: 'road.highway',
 66→                  elementType: 'all',
 67→                  stylers: [{ visibility: 'simplified' }]
 68→                },
 69→                {
 70→                  featureType: 'road.arterial',
 71→                  elementType: 'labels.icon',
 72→                  stylers: [{ visibility: 'off' }]
 73→                },
 74→                {
 75→                  featureType: 'transit',
 76→                  elementType: 'all',
 77→                  stylers: [{ visibility: 'off' }]
 78→                },
 79→                {
 80→                  featureType: 'water',
 81→                  elementType: 'all',
 82→                  stylers: [{ color: '#e74c3c' }, { visibility: 'on' }]
 83→                }
 84→              ]
 85→            }
 86→
 87→            if (!window.google?.maps?.Map || !window.google?.maps?.Marker || !mapRef.current) return
 88→            mapInstanceRef.current = new window.google.maps.Map(mapRef.current, mapOptions)
 89→
 90→            new window.google.maps.Marker({
 91→              position: location,
 92→              map: mapInstanceRef.current,
 93→              title: 'Bengali Society of Melbourne'
 94→            })
 95→          } else {
 96→            console.log('Geocoding failed:', status)
 97→            // Fallback to Point Cook coordinates (approximate)
 98→            if (!window.google?.maps?.LatLng) return
 99→            const pointCookLocation = new window.google.maps.LatLng(-37.8904, 144.7602)
100→            
101→            const mapOptions = {
102→              zoom: 12,
103→              center: pointCookLocation,
104→              mapTypeId: 'roadmap',
105→              scrollwheel: false,
106→              navigationControl: false,
107→              mapTypeControl: false,
108→              scaleControl: false,
109→              draggable: true
110→            }
111→
112→            if (!window.google?.maps?.Map || !window.google?.maps?.Marker || !mapRef.current) return
113→            mapInstanceRef.current = new window.google.maps.Map(mapRef.current, mapOptions)
114→            
115→            new window.google.maps.Marker({
116→              position: pointCookLocation,
117→              map: mapInstanceRef.current,
118→              title: 'Bengali Society of Melbourne - Point Cook Area'
119→            })
120→          }
121→        })
122→      } catch (error) {
123→        console.log('Map initialization error:', error)
124→      }
125→    }
126→
127→    // Wait for Google Maps to load
128→    const waitForGoogle = () => {
129→      return new Promise<void>((resolve) => {
130→        console.log('Checking Google Maps API...', { google: !!window.google, maps: !!window.google?.maps, places: !!window.google?.maps?.places });
131→        
132→        if (window.google && window.google.maps) {
133→          console.log('Google Maps API ready immediately');
134→          resolve()
135→        } else {
136→          console.log('Waiting for Google Maps API to load...');
137→          let attempts = 0;
138→          const checkInterval = setInterval(() => {
139→            attempts++;
140→            console.log(`Checking Google Maps API... Attempt ${attempts}`, { google: !!window.google, maps: !!window.google?.maps, places: !!window.google?.maps?.places });
141→            
142→            if (window.google && window.google.maps) {
143→              clearInterval(checkInterval)
144→              console.log('Google Maps API loaded after', attempts, 'attempts');
145→              resolve()
146→            } else if (attempts > 50) { // Stop after 5 seconds
147→              clearInterval(checkInterval)
148→              console.error('Google Maps API failed to load after 5 seconds');
149→              resolve() // Resolve anyway to avoid hanging
150→            }
151→          }, 100)
152→        }
153→      })
154→    }
155→
156→    waitForGoogle().then(() => {
157→      setTimeout(initMap, 200) // Small delay to ensure everything is ready
158→    })
159→  }, [address, zoom])
160→
161→  return (
162→    <div
163→      ref={mapRef}
164→      className={`w-full h-full min-h-[300px] rounded-lg ${className}`}
165→      style={{ minHeight: '300px' }}
166→    />
167→  )
168→}

