 5→export default function GoogleMapsScript() {
 6→  const apiKey = process.env.NEXT_PUBLIC_GOOGLE_MAPS_API_KEY;
 7→  
 8→  // Debug logging
 9→  console.log('Google Maps API Key status:', {
10→    hasKey: !!apiKey,
11→    keyLength: apiKey?.length || 0,
12→    keyStart: apiKey?.substring(0, 10) || 'NOT_FOUND',
13→    environment: process.env.NODE_ENV
14→  });
15→
16→  return (
17→    <Script
18→      id="google-maps"
19→      strategy="lazyOnload"
20→      src={`https://maps.googleapis.com/maps/api/js?key=${apiKey || 'API_KEY_NOT_FOUND'}&libraries=places`}
21→      onLoad={() => {
22→        console.log('Google Maps API loaded successfully');
23→      }}
24→      onError={(e) => {
25→        console.error('Failed to load Google Maps API:', e);
