27→      }}
28→    />
29→  )
30→}

 1→// Centralized Google Maps type definitions
 2→
 3→export interface GooglePlace {
 4→  formatted_address?: string
 5→  geometry?: {
 6→    location: {
 7→      lat: () => number
 8→      lng: () => number
 9→    }
10→  }
11→  place_id?: string
12→}
13→
14→export interface GoogleAutocompleteOptions {
15→  componentRestrictions?: { country: string }
16→  fields?: string[]
17→  types?: string[]
18→}
19→
20→declare global {
21→  interface Window {
22→    google?: {
23→      maps?: {
24→        Map: new (element: HTMLElement, options: Record<string, unknown>) => unknown
25→        Marker: new (options: Record<string, unknown>) => unknown
26→        Geocoder: new () => {
27→          geocode: (request: Record<string, unknown>, callback: (results: unknown[], status: string) => void) => void
28→        }
29→        LatLng: new (lat: number, lng: number) => unknown
30→        places?: {
31→          Autocomplete: new (
32→            input: HTMLInputElement,
33→            options?: GoogleAutocompleteOptions
34→          ) => {
35→            addListener: (event: string, callback: () => void) => void
36→            getPlace: () => GooglePlace
37→          }
38→        }
39→        event?: {
40→          clearInstanceListeners: (instance: unknown) => void
41→        }
42→      }
43→    }
44→  }
45→}
